body:
/* Toolbars */
--toolbar-top: #2d3031;
--toolbar-bottom: #2d3031;
--toolbar-inactive: #2d3031;

/* Tabs */
--toolbar-tab-active: #2d3031;
--toolbar-tab-inactive: #2d3031;

/* Toolbar Buttons */
--toolbar-button-default: #2d3031;
--toolbar-button-selected: rgb(69,73,75);
--toolbar-button-depressed: #2d3031;
--toolbar-button-border: transparent;

/* Favorites Bar Buttons */
--favbar-button-selected: #2d3031;
--favbar-button-hover: #2d3031;

/* Backgrounds */
--bg-primary: #2d3031;
--bg-secondary: #2d3031;
--bg-sidebar: #2d3031;

/* Dividers */
--divider-dark: #2d3031;
--divider-light: #2d3031;

/* Selectors */
--selector: #484848;
--selector-sidebar: #484848;
--selector-inactive: #393939;
--selector-sidebar-inactive: #393939;
--selector-thread-inactive: #393939:;
--selector-thread-child: #393939;

/* Text */
--font-primary: #d9caaa;
--font-primary-sidebar: #d9caaa;
--font-secondary: #d9caaa;
--font-secondary-sidebar: #d9caaa;
--font-primary-selected: #d9caaa;
--font-secondary-selected: #d9caaa;
--font-pending: #d9caaa;

/* Text Fields */
--text-field-bg: #d9caaa;
--text-field-border: #d9caaa;

/* Folder Pane Bubbles */
--folder-bubble-bg: #d9caaa;
--folder-bubble-bg-selected: #d9caaa;
--folder-bubble-bg-focus: #d9caaa;
--folder-bubble-text: #393939;
--folder-bubble-text-selected: #393939;
--folder-bubble-text-focus: #393939;

/* Focus Pane Bubbles */
--focus-bubble-bg: #2d3031;
--focus-bubble-bg-selected: #2d3031;
--focus-bubble-bg-focus: #2d3031;
--focus-bubble-text: #2C353A;
--focus-bubble-text-selected: #2C353A;
--focus-bubble-text-focus: #2d3031;

/* Message List Bubbles */
--message-bubble-bg: #d9caaa;
--message-bubble-bg-selected: #d9caaa;
--message-bubble-bg-focus: #d9caaa;
--message-bubble-text: #161515;
--message-bubble-text-selected: #161515;
--message-bubble-text-focus: #161515;

/* Notification Bars */
--notification-bg-blue: #F5C223;
--notification-text-blue: #FFFFFF;
--notification-bg-yellow: #F5C223;
--notification-text-yellow: #FFFFFF;
--notification-bg-red: #F43826;
--notification-text-red: #FFFFFF;
