--toolbar-inactive: #282828;

/* Tabs */
--toolbar-tab-active: #282828;
--toolbar-tab-inactive: #3c3836;

/* Toolbar Buttons */
--toolbar-button-default: #282828;
--toolbar-button-selected: #1d2021;
